const items = $input.all();

if (!items || items.length === 0) {
  return [
    {
      json: {
        status: "error",
        error_code: "EMPTY_FILE",
        message: "Uploaded CSV contains no rows"
      }
    }
  ];
}

const rows = items.map(item => item.json);

if (!rows[0] || typeof rows[0] !== "object") {
  return [
    {
      json: {
        status: "error",
        error_code: "INVALID_SCHEMA",
        message: "CSV structure invalid"
      }
    }
  ];
}

const columns = Object.keys(rows[0]);

const firstItem = items[0];

const fileName =
  firstItem.binary?.data0?.fileName || "unknown_dataset.csv";

return [
  {
    json: {
      dataset_name: fileName,
      row_count: rows.length,
      columns: columns,
      rows: rows
    }
  }
];

const backend = items[0].json || {};

const status = backend.status || "unknown";
const summary = backend.summary || null;
const missing = backend.missing_values || null;

// Backend should echo dataset_name (if not, we pass it through HTTP body earlier)
const dataset = backend.dataset_name || "unknown";
const rowCount = backend.row_count || 0;

return [
  {
    json: {
      success: status === "success",
      dataset: dataset,
      analysis: status === "success"
        ? {
            total_rows: summary?.total_rows ?? rowCount,
            total_columns: summary?.total_columns ?? 0,
            missing_values: missing ?? {}
          }
        : null,
      error: status !== "success"
        ? {
            code: "ANALYSIS_FAILED",
            message: "Backend analysis failed",
            source: "backend"
          }
        : null,
      timestamp: new Date().toISOString()
    }
  }
];

const response = items[0].json;

// Log structured metadata
console.log("========== CSV ANALYSIS RESPONSE ==========");
console.log("Success:", response.success);
console.log("Dataset:", response.dataset);
console.log("Rows:", response.analysis?.total_rows ?? 0);
console.log("Columns:", response.analysis?.total_columns ?? 0);
console.log("Timestamp:", response.timestamp);
console.log("===========================================");

// Always pass data forward unchanged
return items;
